import { Component, OnInit } from '@angular/core';
import { FormGroup, FormControl, ReactiveFormsModule, Validators } from '@angular/forms';
import { CardModalComponent } from '../../../shared/pop-up-card/card-modal/card-modal.component';
import { ToggleDeleteModalService } from '../../../services/toggleModal/toggle-delete-modal.service';
import { CommonModule } from '@angular/common';


@Component({
  selector: 'app-add-user',
  standalone: true,
  imports: [ReactiveFormsModule, CardModalComponent, CommonModule],
  templateUrl: './add-user.component.html',
  styleUrl: './add-user.component.css'
})
export class AddUserComponent implements OnInit {
  isavailable!: Boolean;
  addAccountModal!: Boolean;


  addUser: FormGroup = new FormGroup({
    fullName: new FormControl(null, [Validators.required]),
    email: new FormControl(null, [Validators.required]),
    password: new FormControl(null),
    confirmPassword: new FormControl(null),
    roleId: new FormControl(null),
    profileImagePath: new FormControl(null),
  })

  constructor(private _ToggleAddModalService: ToggleDeleteModalService) { }


  ngOnInit(): void {
    this._ToggleAddModalService.getToggleValue().subscribe({
      next: (toggler) => {
        this.isavailable = toggler;
      }
    })
  }


  close() {
    this.isavailable = false;
    this._ToggleAddModalService.toggle();
  }


  AddUser() {
    console.log(this.addUser)
  }


}

import { Component, OnInit } from '@angular/core';
import { RouterOutlet } from '@angular/router';
import { SignupComponent } from './components/Authentication/signup/signup.component';

import { ReactiveFormsModule } from '@angular/forms';
import { style, transition, trigger, group, query, animate } from '@angular/animations';
import { NavigationService } from './globalAnimation/navigation.service';
import { AuthService } from './services/auth.service';
import { LocalStorageKeys } from './keys/local-storage-keys';
import { AppUser } from './interfaces/app-user';

@Component({
  selector: 'app-root',
  standalone: true,
  imports: [RouterOutlet, SignupComponent, ReactiveFormsModule],
  templateUrl: './app.component.html',
  styleUrl: './app.component.css',
  animations: [
    trigger('routerTransition', [
      transition('* <=> *', [
        query(':enter, :leave', style({ position: 'fixed', width: '100%' }), { optional: true }),
        group([
          query(':enter', [
            style({ transform: 'translateX(100%)' }),
            animate('0.7s ease-in-out', style({ transform: 'translateX(0%)' }))
          ], { optional: true }),
          query(':leave', [
            style({ transform: 'translateX(0%)' }),
            animate('0.7s ease-in-out', style({ transform: 'translateX(-100%)' }))], { optional: true }),
        ])
      ]),
    ])
  ],
})
export class AppComponent implements OnInit {
  constructor(private navigationService: NavigationService, private _AuthService: AuthService) { }

  ngOnInit(): void {
    if (localStorage.getItem(LocalStorageKeys.USER_SESSION)) {
      this._AuthService.setUserOnBootstrap(JSON.parse(localStorage.getItem(LocalStorageKeys.USER_SESSION) || ""));
    }
  }

  getState(outlet: any) {
    return this.navigationService.animationValue;
  }

  title = 'crmproject';
}

