import { Component, DoCheck, OnInit } from '@angular/core';
import {
  FormBuilder,
  FormControl,
  FormGroup,
  FormsModule,
  ReactiveFormsModule,
  Validators,
} from '@angular/forms';
import { Router, RouterModule } from '@angular/router';
import { CommonModule } from '@angular/common';
import { animate, style, transition, trigger } from '@angular/animations';
import { AuthService } from '../../../services/auth.service';
import { AutoFocusDirective } from '../directives/auto-focus.directive';
import { SpinnerComponent } from '../../../shared/spinner/spinner/spinner.component';

@Component({
  selector: 'app-email-verification',
  standalone: true,
  imports: [RouterModule, FormsModule, ReactiveFormsModule, CommonModule, AutoFocusDirective, SpinnerComponent],
  templateUrl: './email-verification.component.html',
  styleUrl: './email-verification.component.css',
  animations: [
    trigger('flipInOut', [
      transition(':enter', [
        animate('1s', style({ transform: 'rotateY(90deg)' })),
      ]),
      transition(':leave', [
        animate('1s', style({ transform: 'rotateY(180deg)' })),
      ]),
    ]),
  ],
})
export class EmailVerificationComponent implements OnInit, DoCheck {
  str1!: string;
  str2!: string;
  str3!: string;
  str4!: string;
  str5!: string;
  str6!: string;
  email!: string | undefined;
  otp: string = '';
  errorExist: boolean = false;
  resendOTPBool!: Boolean;
  resendOtpMsg!: string;
  spinner!: Boolean

  public slides: string[] = [
    'assets/images/illustration-signup.svg',
    'assets/images/Frame 1000016152.svg',
  ];

  formVerification = new FormGroup({
    verificationCode1: new FormControl(''),
    verificationCode2: new FormControl(''),
    verificationCode3: new FormControl(''),
    verificationCode4: new FormControl(''),
    verificationCode5: new FormControl(''),
    verificationCode6: new FormControl(''),
  });
  constructor(
    private formBuilder: FormBuilder,
    private _authService: AuthService,
    private _Router: Router
  ) {
    this.resendOTPBool = false;
    this.spinner = false;
  }
  ngDoCheck(): void { }
  ngOnInit(): void {
    this.email = sessionStorage?.getItem('email')?.slice(0, 4).concat("************");

    this.formVerification = this.formBuilder.group({
      verificationCode1: ['', [Validators.required, Validators.maxLength(1)]],
      verificationCode2: ['', [Validators.required, Validators.maxLength(1)]],
      verificationCode3: ['', [Validators.required, Validators.maxLength(1)]],
      verificationCode4: ['', [Validators.required, Validators.maxLength(1)]],
      verificationCode5: ['', [Validators.required, Validators.maxLength(1)]],
      verificationCode6: ['', [Validators.required, Validators.maxLength(1)]],
    });
  }

  // send otp
  verificationSubmit() {
    this.spinner = true;
    if (this.formVerification.invalid) {
      this.spinner = false;
      console.log(this.formVerification);
      return;
    }

    this.otp = `${this.str1}${this.str2}${this.str3}${this.str4}${this.str5}${this.str6}`;
    this._authService.resetVerificationCode(this.otp).subscribe({
      next: (res) => {
        this.spinner = false;
        sessionStorage.setItem('token', res.token);
        this._Router.navigateByUrl('setnewpassword');
      },
      error: (err) => {
        this.spinner = false;
        this.errorExist = true;
        this.resendOTPBool = false;

      },
    });

  }

  resendOTP() {
    this.errorExist = false;
    this.resendOTPBool = true;
    this.spinner = true;
    this._authService.ResendOTPForgetPassword().subscribe({
      next: (res: any) => {
        this.resendOtpMsg = res.message;
        this.spinner = false;
      },
      error: (err) => {
        this.spinner = false;
        this.resendOtpMsg = err.error.message;
      }
    })
  }

  removeErrorExit() {
    this.errorExist = false;
  }
}

import { Component } from '@angular/core';
import { ToggleFilterDirective } from '../../directives/toggle-filter.directive';
import { FormArray, FormBuilder, FormControl, FormGroup, FormsModule, ReactiveFormsModule, Validators } from '@angular/forms';
import { CommonModule } from '@angular/common';

@Component({
  selector: 'app-filter',
  standalone: true,
  imports: [ToggleFilterDirective, ReactiveFormsModule, FormsModule, CommonModule],
  templateUrl: './filter.component.html',
  styleUrl: './filter.component.css'
})
export class FilterComponent {

  toggleAddFilterBtn!: Boolean;
  filterForm!: FormGroup;
  filteredArray!: FormArray;
  cols: string[] = ["Blog Title", "Status", "SEO", "Created At", "Created By", "Author"]
  tempCols!: string[];
  filterSearch: string = "";
  constructor(private _FormBuilder: FormBuilder) {
    this.toggleAddFilterBtn = false;
    this.tempCols = this.cols;
  }

  ngOnInit(): void {
    this.fb();
  }

  fb() {
    this.filterForm = this._FormBuilder.group({
      columnId: [null, Validators.required],
      columnName: [null, Validators.required],
      columnValue: [null, Validators.required],
      condition: [null, Validators.required]
    })
  }

  toggleAddBtn() {
    this.toggleAddFilterBtn = !this.toggleAddFilterBtn;
  }

  filter(event: any) {
    if (this.filterSearch) {
      this.tempCols
        = this.cols.filter(column => column.toLowerCase().includes(this.filterSearch));
    }
    else if (event.inputType == "deleteContentBackward" || event.inputType == "deleteContentForward") {
      this.tempCols = this.cols
      this.cols = this.cols.filter(column => column.toLowerCase().includes(this.filterSearch));
    }
    else {
      this.tempCols = this.cols
    }
  }

  addFilter(columnIndex: number): void {
    // (<FormArray>this.filteredArray).push(
    //   new FormGroup(
    //     {
    //       columnid: new FormControl(null, [Validators.required]),
    //       columnName: new FormControl([this.cols[columnIndex], [Validators.required]]),
    //       condition: new FormControl(null, [Validators.required]),
    //       columnValue: new FormControl(null, [Validators.required]),
    //     }
    //   )
    // )
  }


  get controls() {
    return (<FormArray>this.filterForm.get('filteredArray')).controls;
  }

}

