src/app/token.interceptor.ts.defaultValue:
(req, next) => {
  const token = inject(AuthService).Token;
  if (token) {
    req = req.clone({
      headers: req.headers
        .set('Authorization', `Bearer ${token}`)
        .append('Content-Type', 'text/plain')
        .append('Access-Control-Allow-Origin', '*'),
    });
  }
  return next(req);
}
