import { HttpClient } from '@angular/common/http';
import { Injectable } from '@angular/core';
import { Observable } from 'rxjs';
import { environment } from '../../environments/environment';
import { ISignupRequest } from '../interfaces/signupinterface';
import { ILogin } from '../interfaces/logininterface';
import { ISignUpResponse } from '../interfaces/isign-up-response';

@Injectable({
  providedIn: 'root',
})
export class AuthService {
  constructor(private http: HttpClient) { }

  signUp(registerData: ISignupRequest): Observable<ISignUpResponse> {
    console.log(registerData);
    return this.http.post<ISignUpResponse>(
      environment.BASEURL + 'api/Authentication/Register',
      registerData
    );
  }

  verificationCode(otp: string): Observable<any> {
    var obj = {
      email: sessionStorage.getItem('email')?.toString(),
      otp: otp.toString(),
      token: sessionStorage.getItem('token')?.toString(),
    };
    console.log(obj);
    return this.http.post(
      `${environment.BASEURL}api/Authentication/ConfirmEmail`,
      obj
    );
  }

  resendOTP() {
    var obj = {
      email: sessionStorage.getItem('email')?.toString(),
    };
    console.log(obj);
    return this.http.post(
      environment.BASEURL + `api/Authentication/ResendOTP?email=${obj.email}`,
      {}
    );
  }

  setToken(token: string) {
    localStorage.setItem('token', token);
  }

  setTokenInSessionStorage(token: string) {
    sessionStorage.setItem('token', token);
  }

  get isLoggedIn(): boolean {
    // return true;
    const userData = localStorage.getItem(localStorage['token'].userSession);
    console.log(userData);
    if (userData) {
      const jsonUserData = JSON.parse(userData);
      const token = jsonUserData[localStorage['token']];

      const tokenExpireDate =
        jsonUserData[localStorage['token'].tokenExpireDate];
      return token && new Date(tokenExpireDate) >= new Date();
    }
    return false;
  }

  saveUserSession(applicationUser: any) {
    localStorage.setItem(
      localStorage['userSession'],
      JSON.stringify(applicationUser)
    );
  }

  logout() {
    localStorage.removeItem(localStorage['userSession']);
  }

  get Name() {
    if (this.isLoggedIn) {
      let userData = localStorage.getItem(localStorage['userSession']);
      if (userData) return JSON.parse(userData)[localStorage['fullName']];
      return null;
    }
  }

  get Token() {
    if (this.isLoggedIn) {
      let userData = localStorage.getItem(localStorage['userSession']);
      if (userData) return JSON.parse(userData)[localStorage['token']];
      return null;
    }
  }


  sendMail(email: string): Observable<any> {
    console.log(email);
    return this.http.post(
      environment.BASEURL + `api/Authentication/ForgetPassword?email=${email}`,
      {}
    );
  }


  resetVerificationCode(res: string): Observable<any> {
    var obj = {
      email: sessionStorage.getItem('email')?.toString(),
      otp: res.toString(),
      //token: sessionStorage.getItem('token')?.toString(),
    };
    console.log(obj);
    return this.http.post(
      `${environment.BASEURL}api/Authentication/ConfirmForgetPasswordOTP`,
      obj
    );
  }


  Login(request: ILogin): Observable<any> {
    console.log(request);
    return this.http.post<any>(
      environment.BASEURL + 'api/Authentication/Login',
      request
    );
  }




  // setNewPass(res:IForgetPassRequest){
  //   var obj={
  //     email: sessionStorage.getItem('email')?.toString(),
  //     token: sessionStorage.getItem('token')?.toString(),
  //     message: sessionStorage.getItem('message')?.toString(),
  //     password: res.password.toString(),
  //     confirmNewPassword: res.confirmNewPassword.toString()
  //   }
  //   console.log(obj);
  //   return this.http.post(
  //     environment.BASEURL + `api/Authentication/ConfirmForgetPassword`, obj);
  // }

  forgetPassword(email: string) {
    console.log(email);
    return this.http.post<any>(
      `${environment.BASEURL}api/Authentication/ForgetPassword?email=${email}`,
      {}
    );
  }
}

import { Injectable } from '@angular/core';
import { LocationStrategy } from '@angular/common';
import { BehaviorSubject, Observable } from 'rxjs';


@Injectable({
  providedIn: 'root'
})


export class BreadcrumbService {
  private fullPath!: BehaviorSubject<string[]>;
  constructor(private _LocationStrategy: LocationStrategy) {
    this.fullPath = new BehaviorSubject<string[]>([]);
  }

  ngOnInit() {
    this.fullPath.next(this._LocationStrategy.path().split('/'));
  }

  changeCurrentPath() {
    this.fullPath.next(this._LocationStrategy.path().split('/'));
  }

  getCurrentPath(): Observable<any> {
    this.fullPath.next(this._LocationStrategy.path().split('/'));
    return this.fullPath;
  }
}

import { HttpClient } from '@angular/common/http';
import { Injectable } from '@angular/core';
import { Observable } from 'rxjs';
import { environment } from '../../environments/environment';
import { IForgetPassRequest } from '../interfaces/iforget-pass-request';

@Injectable({
  providedIn: 'root'
})
export class ResetPasswordService {
  authenticationURL!: string;
  baseURL!: string;

  constructor(private _HttpClient: HttpClient) {
    this.baseURL = environment.BASEURL;
    this.authenticationURL = '/api/Authentication'
  }

  resetPassword(res:IForgetPassRequest): Observable<any> {
    const obj = {
      email: sessionStorage.getItem('email')?.toString(),
      token: sessionStorage.getItem('token')?.toString(),
      // message: sessionStorage.getItem('message')?.toString(),
      newPassword: res.password.toString(),
      confirmNewPassword: res.confirmNewPassword.toString()
    }
    return this._HttpClient.post<any>(`${this.baseURL}api/Authentication/ConfirmForgetPassword`, obj);
  }
}

import { Injectable } from '@angular/core';
import { BehaviorSubject, Observable } from 'rxjs';

@Injectable({
  providedIn: 'root'
})
export class ToggleDeleteModalService {

  constructor() { }

  private toggler: BehaviorSubject<Boolean> = new BehaviorSubject<Boolean>(false);

  private filterToggler: BehaviorSubject<Boolean> = new BehaviorSubject<Boolean>(false);

  private sortToggler: BehaviorSubject<Boolean> = new BehaviorSubject<Boolean>(false);

  toggle() {
    this.toggler.next(!this.toggler.value);
  }

  getToggleValue(): Observable<Boolean> {
    return this.toggler;
  }

  toggleFilter() {
    this.filterToggler.next(!this.filterToggler.value);
  }

  getFilterValue(): Observable<Boolean> {
    return this.filterToggler;
  }
  toggleSort() {
    this.sortToggler.next(!this.sortToggler.value);
  }

  getSortValue(): Observable<Boolean> {
    return this.sortToggler;
  }
}

